    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
       //numero 1 e numero 2 recebe integerparseint txtnumero (campo de texto)
       // l√≥gico: true/false
       // texto: string
       // numero decimal: float e double (numeros com virgula)
       //date: datas (util e sql)
        
        int num1= Integer.parseInt(txtNumero1.getText());
        int num2= Integer.parseInt(txtNumero2.getText());
        
        //exibir o resultado dos dois numeros, usar o paresenteses para somar e depois multiplicar
        
        int resultado = (num1 + num2) * 3;
        
        // exibir a mensagem mais o resultado
        
        String mensagem = lblResultado.getText() +" "+ resultado;
        
        lblResultado.setText(mensagem);
    }//GEN-LAST:event_btnCalcularActionPerformed

